catalog_script_client.script:
function onLoad() {
	var verbotenParams = ['id'];
	var url = this.location.href || window.location.search.slice(1);
	var queryString = url.split('?')[1].split('#')[0];
	if (queryString) {
		var sys_id;
		var obj = {};
		var arr = queryString.split('&');
		for (var i = 0; i < arr.length; i++) {
			var param = arr[i].split('=');
			var paramName = param[0];
			var paramValue = param[1] ? param[1] : '';

			if (verbotenParams.indexOf(paramName) > -1) continue;

			if (paramName == 'sys_id') {
				sys_id = paramValue.toString();
				continue;
			}

			try {
				obj[paramName] = paramValue;
				if (g_form.hasField(paramName.toString())) {
					g_form.setValue(paramName.toString(), decodeURIComponent(paramValue.toString()));
				}
			} catch (e) {
				if (g_user.hasRole('admin')) {
					console.error('Error setting parameter value from URI: ' + e);
				}
			}
		}
	}
}
