sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	if (newValue === '') {
		g_form.clearValue('url');
		g_scratchpad.portalSuffix = '';
	} else {
		g_form.getReference('portal', callback_Portal);
	}

}

function callback_Portal(portal) {
	var portalSuffix = portal.getValue('url_suffix');
	var spPage = g_scratchpad.spPage;
	var url = '/' + portalSuffix + '?id=' + spPage;

	// this is not sent back to the server; update it so the new value is available to other client side scripts
	g_scratchpad.portalSuffix = portalSuffix;

	var params = {};
	params.sys_id = g_scratchpad.catItem;
	var pairs = g_form.getValue('variable_pairs');
	if (pairs) {
		pairs = JSON.parse(pairs);
		for (var key in pairs) {
			params[key] = pairs[key];
		}
	}

	for (var p in params) {
		url += '&' + p + '=' + params[p];
	}

	g_form.setValue('url', url);

}
