sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	if (newValue === '') {
		g_form.clearValue('url');
		g_scratchpad.catItem = '';
		g_scratchpad.spPage = '';
	} else {
		g_form.getReference('cat_item', callback_CatItem);
	}

}

function callback_CatItem(item) {	
	var portalSuffix = g_scratchpad.portalSuffix || g_scratchpad.defaultPortalSuffix;
	var spPage = item.sys_class_name == 'sc_cat_item_guide' ? 'sc_cat_item_guide' : 'sc_cat_item';
	var url = '/' + portalSuffix + '?id=' + spPage;

	// these are not sent back to the server; update so the new values are available to other client side scripts
	g_scratchpad.catItem = item.sys_id;
	g_scratchpad.spPage = spPage;

	var params = {};
	params.sys_id = item.sys_id;
	var pairs = g_form.getValue('variable_pairs');
	if (pairs) {
		pairs = JSON.parse(pairs);
		for (var key in pairs) {
			params[key] = pairs[key];
		}
	}

	for (var p in params) {
		url += '&' + p + '=' + params[p];
	}

	g_form.setValue('url', url);

}
